class NewsTagFlag extends BaseNewsTagFlag
{
  public function postSave($event)
  {
    $tag = $this->Item;
    $cacheDriver = $this->getTable()->getAttribute(Doctrine_Core::ATTR_RESULT_CACHE);

    if ($tag->status == 'Flagged')
      $cacheDriver->delete('news_tags_'.$tag->item_id);
  }
}